function formatDate(n,t){return t=="ddMMMyyyy"?ddMMMyyyy(n):t=="yyyyMMdd"?yyyyMMdd(n):n}function yyyyMMdd(n){var r=new Date(n),t=""+(r.getMonth()+1),i=""+r.getDate(),u=r.getFullYear();return t.length<2&&(t="0"+t),i.length<2&&(i="0"+i),[u,t,i].join("-")}function ddMMMyyyy(n){var t=parseDate(n),i=t.getDate(),r=t.getMonth(),u=t.getFullYear();return(i<10?"0"+i:i)+"-"+["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"][r]+"-"+u}function parseDate(n){var t=n.match(/(\d+)/g);return new Date(t[0],t[1]-1,t[2])}Date.prototype.getWeek=function(){var n=new Date(this.getFullYear(),0,1),t=new Date(this.getFullYear(),this.getMonth(),this.getDate()),i=(t-n+1)/864e5;return Math.ceil(i/7)};String.prototype.replaceAll=function(n,t){var i=this;return i.replace(new RegExp(n,"g"),t).replace(/^ +| +$/gm,"")};